mapper.update:
UPDATE board
   set title = #{title}
      ,writer = #{writer}
      ,content = #{content}
      ,upd_date = sysdate
WHERE board_no = #{boardNo}

mapper.select:


SELECT *
FROM (
    SELECT a.*, ROWNUM rno
    FROM board a
    ORDER BY board_no DESC
) b
WHERE b.rno >= #{startRowNo}
  AND b.rno <= #{endRowNo}
